import { Component } from '@angular/core';
import { ButtonComponent } from './button/button.component';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
  standalone: true,
  imports: [ButtonComponent]
})
export class AppComponent {
  title = 'angular-app';

  onPrimaryClick() {
    alert('Primary button clicked!');
  }

  onSecondaryClick() {
    alert('Secondary button clicked!');
  }
}
<h1>Angular Button Demo</h1>

<div style="margin-bottom: 16px">
  <app-button variant="primary" label="Primary Button" (click)="onPrimaryClick()"></app-button>
</div>

<div>
  <app-button variant="secondary" label="Secondary Button" (click)="onSecondaryClick()"></app-button>
</div>
